import React, {useState} from 'react';
import Paper from '@material-ui/core/Paper';
import TextField from '@material-ui/core/TextField';
import { Button, Typography } from '@material-ui/core';
import NoteAddIcon from '@material-ui/icons/NoteAdd';

import axios from 'axios';

function UusiTyontekija() {

    const [tyontekija, setValues] = useState({
        etunimi:'',
        sukunimi:''
    });
    const [viesti, setViesti] = useState('');

    const muuta = (e) => {
        setValues ({
            ...tyontekija,
            [e.target.name]: e.target.value
        });
    };
    
    const url= 'http://localhost:8080'

    const lisaaTyontekija = (e) => {
        e.preventDefault();
        const formData = {
            'etunimi':tyontekija.etunimi,
            'sukunimi':tyontekija.sukunimi
        }
        axios.post( url + '/kanta/tyontekijat/add', formData)
        .then(response => {
            if (response.status === 200){
                setValues({
                    ...tyontekija, [tyontekija.etunimi]:'', [tyontekija.sukunimi]:''
                })
            }else{
                setViesti('Lis채ys ei onnistunut')
            }
        })
    }

    return (
        <div>
            <Paper style={{width:250}}>
                <form style={{padding:20}}>

                <TextField 
                required
                fullWidth 
                label='Etunimi' 
                name='etunimi'
                value={tyontekija.etunimi}
                onChange = {(e) => muuta(e)}/>
                
                <TextField 
                required
                fullWidth 
                name='sukunimi'
                label='Sukunimi' 
                value={tyontekija.sukunimi}
                onChange = {(e) => muuta(e)}/>

                </form>
                <Button 
                variant='contained' 
                startIcon={<NoteAddIcon />} 
                color='secondary'
                onClick={lisaaTyontekija}
                align='center'
                >Lis채채</Button>

                <Typography> {viesti}</Typography>
            </Paper>
        </div>
    )
}

export default UusiTyontekija;
