import {React, useState} from 'react';
import UusiTilaus from './components/UusiTilaus';
import Menu from './menu/Menu';
import HaeTilaukset from './components/HaeTilaukset';
import HaeTyontekijat from './components/HaeTyontekijat';
import TilausEdit from './components/TilausEdit';
import Kirjaudu from './components/Kirjaudu';
import UusiTyontekija from './components/UusiTyontekija';
import HaeAsiakkaat from './components/HaeAsiakkaat';
import UusiAsiakas from './components/UusiAsiakas';

import { makeStyles } from '@material-ui/core/styles';
import {blue, deepPurple, cyan } from '@material-ui/core/colors/';
import FaceIcon from '@material-ui/icons/Face';

import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';

import { BrowserRouter, Route, Switch } from 'react-router-dom';
import CssBaseline from '@material-ui/core/CssBaseline';

import AppBar from '@material-ui/core/AppBar'
import Toolbar from '@material-ui/core/Toolbar';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';

import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import { Button, IconButton, TextField } from '@material-ui/core';
import Visibility from '@material-ui/icons/Visibility';
import VisibilityOff from '@material-ui/icons/VisibilityOff';
import InputLabel from '@material-ui/core/InputLabel';
import InputAdornment from '@material-ui/core/InputAdornment';
import Input from '@material-ui/core/Input';
import DoneIcon from '@material-ui/icons/Done';
import ExitToAppIcon from '@material-ui/icons/ExitToApp';
import MeetingRoomIcon from '@material-ui/icons/MeetingRoom';
import Lasku from './components/Lasku';




const tyyli = makeStyles ({
  otsikko: {
    height: 70,
    backgroundColor: blue[50]
  }
})

function App() {
  const classes = tyyli();

  const [tabArvo, setTab] = useState(0);

  const muutaTab = (e, val) => {
  setTab(val)};

  const [open, setOpen] = useState(false)

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const [kayttajatunnus, setKayttis] = useState ('');
  const [salasana, setSalasana] = useState('');
  const muutaKayttis = (e) => {
    setKayttis(e.target.value)
  }
  const muutaSalis = (e) => {
    setSalasana(values.password)
  }
  const [values, setValues] = useState({
    amount: '',
    password: '',
    weight: '',
    weightRange: '',
    showPassword: false,
  });

  const handleClickShowPassword = () => {
    setValues({ ...values, showPassword: !values.showPassword });
  };

  const handleMouseDownPassword = (event) => {
    event.preventDefault();
  };
  const handleChange = (prop) => (event) => {
    setValues({ ...values, [prop]: event.target.value });
  };

  const [kirjautunut, setKirjautunut] = useState(false);
  const muutaKirjautunut = (e) =>{
    setKirjautunut(true);
  } 

  const [virhe, setVirhe] = useState('');


  const ulos = () => {
    setKirjautunut(false)
    setVirhe('')
  };






  const tarkista = async() => {
      try {
        const response = await fetch('http://localhost:8080/kanta/kayttikset/one/' + kayttajatunnus);
        const json = await response.json();
        console.log(json);

          if (json.salasana === values.password) {
          muutaKirjautunut(true);
          setVirhe('Kirjautuminen onnistui')
          setKayttis('')
          setValues({...values, Password:''})
          handleClose()
          }else {
          setVirhe('Väärä käyttäjätunnus tai salasana')
          }

      }catch (error) {
        setVirhe('Yritä uudelleen')
      }
  }  
  /*const Appbar = (
    <AppBar position='static'>
          <Tabs value={ tabArvo } onChange={ muutaTab } variant='fullWidth' centered >
            <Tab label='UusiTilaus' />
            <Tab label='LISÄÄ ELOKUVA' />
            <Tab label='PÄIVÄKIRJA' />
          </Tabs>
    </AppBar>

    )*/
    if (kirjautunut === false){
  return (
    <BrowserRouter>
    <CssBaseline />
      <div><Grid>
        <Paper >
          <Typography variant='h4' component='h1' align='center'>Labra</Typography>
        </Paper>
            <IconButton onClick={handleClickOpen}>
            <FaceIcon/>
          </IconButton>
          <Dialog open={open} onClose={handleClose}>
          <DialogTitle>Kirjaudu sisään</DialogTitle>
            <TextField 
            fullWidth
            label='käyttäjätunnus'
            variant='outlined'
            value={kayttajatunnus}
            onChange={muutaKayttis}/>
            <InputLabel>Salasana</InputLabel>
            <Input
              type={values.showPassword ? 'text' : 'password'}
              value={values.Password}
              onChange={handleChange('password')}
            endAdornment={
              <InputAdornment position="end">
                <IconButton
                  onClick={handleClickShowPassword}
                  onMouseDown={handleMouseDownPassword}
                >
                  {values.showPassword ? <Visibility /> : <VisibilityOff />}
                </IconButton>
              </InputAdornment>
            }
          />
          <IconButton
            onClick={tarkista}
            align='center'>
            <DoneIcon/>
          </IconButton>
          {virhe}
          <IconButton onClick={handleClose}>
            <ExitToAppIcon/>
          </IconButton>
          </Dialog>
        <Menu/>
        <Switch>
              <Route exact path='/' component={Kirjaudu} />
              {/*<Route path='/tilaukset' 
                render={(props) => <HaeTilaukset {...props} kirjautunut= {kirjautunut}/>}/>
              <Route path='/tyontekijat' 
                render={(props) => <HaeTyontekijat {...props} kirjautunut= {kirjautunut}/>}/>
          <Route path='/muokkaatilausta/:id/:tuote/:tyontekija/:toimpvm/:hinta/:asiakas'  component={ TilausEdit } />*/}
              <Route component={Kirjaudu}/>
            </Switch>
      </Grid>
      </div>
    </BrowserRouter>
  );}else {
    return (
      <BrowserRouter>
      <CssBaseline />
        <div>
          <Paper >
            <Typography variant='h4' component='h1' align='center'>Labra</Typography>
          </Paper>
              <IconButton onClick={handleClickOpen}>
              <FaceIcon/>
            </IconButton>
            <Dialog open={open} onClose={handleClose}>
              <IconButton
              onClick={ulos}>
              <MeetingRoomIcon/>
              Kirjaudu ulos
          </IconButton>
            <IconButton onClick={handleClose}>
              <ExitToAppIcon/>
            </IconButton>
            </Dialog>
          <Menu/>
            <Switch>
              <Route exact path='/' component={HaeTilaukset} />
              <Route path='/uusiTilaus' component={UusiTilaus}/>
              <Route path='/tilaukset' component={HaeTilaukset}/>
              <Route path='/tyontekijat' component={HaeTyontekijat}/>
              <Route path='/muokkaatilausta/:id/:tuote/:tilauspaiva/:tyontekija/:toimpvm/:hinta/:asiakas'  component={TilausEdit} />
              <Route path='/uusiTyontekija' component={UusiTyontekija}/>
              <Route path='/asiakkaat' component={HaeAsiakkaat}/>
              <Route path='/uusiAsiakas' component={UusiAsiakas}/>
              <Route path='/lasku/:id/:asiakas' component={Lasku}/>
              <Route component={UusiTilaus}/>
            </Switch>

        </div>
      </BrowserRouter>
    );

  }
}

export default App;
