import { Input, InputLabel, Paper, TextField, Typography, OutlinedInput } from '@material-ui/core';
import React, {useEffect, useState} from 'react';
import FormControl from '@material-ui/core/FormControl';

function Kayttaja(props){

    const [tunnus, setTunnus] = useState({
        kayttajatunnus:(props.kayttajatunnus),
        salasana:''
    });

    const [uusiSalis, setUusiSalis] = useState('');

    const muutaSalasana = (e) =>{
        setTunnus([
            ...tunnus, {salasana:e.target.value}
        ])
    };

    const muutaUusiSalasana = (e) => {
        setUusiSalis(e.target.value)
    };

    const [vertailuSalis, setVertailu] = useState('');
    const muutaVertailu = (e) => {
        setVertailu(e.target.value)
    };

    const hae = async() => {
        try {
          const response = await fetch('http://localhost:8080/kanta/kayttikset/one/' + tunnus.kayttajatunnus);
          const json = await response.json();
          console.log(json);
        }catch(error){

        }}; 
    return(
        <div>
            {console.log(props.kayttajatunnus)}
            <Paper>
                <form style ={{margin:10, padding:10}}>
                    <FormControl disabled >
                        <InputLabel>{tunnus.kayttajatunnus}</InputLabel>
                        <OutlinedInput value={tunnus.kayttajatunnus} />
                        {console.log(tunnus.kayttajatunnus)}
                    </FormControl>

                    <Typography>Vaihda salasana:</Typography>
                    <FormControl>
                        <InputLabel>Vanha salasana:</InputLabel>
                        <Input value={tunnus.salasana} onChange = {(e) => muutaSalasana(e)}/>
                    </FormControl>
                    <FormControl>
                        <InputLabel>Uusi salasana:</InputLabel>
                        <Input value={uusiSalis} onChange = {(e) => muutaUusiSalasana(e)}/>
                    </FormControl>
                    <FormControl>
                        <InputLabel>Uusi salasana uudestaan:</InputLabel>
                        <Input value={vertailuSalis} onChange = {(e) => muutaUusiSalasana(e)}/>
                    </FormControl>
                </form>
            </Paper>
        </div>
    );
};

export default Kayttaja;
