import React, { useState } from 'react';
import Paper from '@material-ui/core/Paper';
import TextField from '@material-ui/core/TextField';
import Select from '@material-ui/core/Select';
import MenuItem from '@material-ui/core/MenuItem';
import InputLabel from '@material-ui/core/InputLabel';


import {MuiPickersUtilsProvider, KeyboardDatePicker } from '@material-ui/pickers';
import DateFnsUtils from '@date-io/date-fns';
import fiLocale from 'date-fns/locale/fi';
import { FormControl, Typography } from '@material-ui/core';



function UusiTilaus (props) {

    const kirjatunut = props.kirjautunut;

    const [tuote, setTuote] = useState('');
    const muutaTuote = (e) => {
    setTuote(e.target.value);
    };

    const [tilauspaiva, setTilauspaiva] = useState(new Date());
    const muutaPaiva = (date) =>{
        setTilauspaiva(date);
    };

    const [toimpaiva, setTPaiva] = useState(null);
    const muutaTPaiva = (date) => {
        setTPaiva(date);
    };

    const [hinta, setHinta] = useState();
    const muutaHinta = (e) => {
    setHinta(e.target.value);
    };    

    const [tyontekija, setTyontekija] = useState('Työntekijä');
    const muutaTyontekija = (e) => {
    setTyontekija(e.target.value);
    };   

    const [asiakas, setAsiakas] = useState('');
    const muutaAsiakas = (e) =>{
        setAsiakas(e.target.value);
    };
 

    if (kirjatunut){
    return (
        <div>
            <Paper>
                <form style={{padding:20}}>
                <TextField 
                fullWidth 
                label='Tuote' 
                value={tuote}
                onChange = {(e) => muutaTuote}/>

                

                <MuiPickersUtilsProvider utils={DateFnsUtils} locale={fiLocale} >
                    <KeyboardDatePicker label='Tilauspäivä' name='tilauspaiva'
                    fullWidth={true} required
                    value={tilauspaiva}
                     onChange={muutaPaiva}
                    format='dd.MM.yyyy' />
                </MuiPickersUtilsProvider>

                
                <InputLabel id="tyont">Työntekijä</InputLabel>
                <Select
                label='Työntekijä'
                fullWidth
                value={tyontekija}
                onChange={muutaTyontekija}
                labelId='tyont'
                >
                    <MenuItem value={'Matti Meikäläinen'}>Matti Meikäläinen</MenuItem>
                    <MenuItem value={'Maija Meikäläinen'}>Maija Meikäläinen</MenuItem>
                </Select>
                

                <MuiPickersUtilsProvider utils={DateFnsUtils} locale={fiLocale}>
                    <KeyboardDatePicker label='Toimituspäivä' name='toimpaiva'
                    fullWidth={true} required
                    value={toimpaiva}
                     onChange={muutaTPaiva}
                    format='dd.MM.yyyy' />
                </MuiPickersUtilsProvider>

                <TextField 
                fullWidth 
                label='Hinta' 
                value={hinta}
                onChange = {(e) => muutaHinta}/>

                <TextField 
                fullWidth 
                label='Asiakas' 
                variant='outlined'
                value={asiakas}
                onChange = {(e) => muutaAsiakas}/>

                </form>
            </Paper>
        </div>
    )}
    else {
        return(
            <Typography>
                Ole hyvä ja kirjaudu sisään ensin!
            </Typography>
        )
    }
};
export default UusiTilaus;
