import React, { useState, useEffect } from 'react';
import Paper from '@material-ui/core/Paper';
import TextField from '@material-ui/core/TextField';
import Select from '@material-ui/core/Select';
import MenuItem from '@material-ui/core/MenuItem';
import InputLabel from '@material-ui/core/InputLabel';
import Typography from '@material-ui/core/Typography';

import {MuiPickersUtilsProvider, KeyboardDatePicker } from '@material-ui/pickers';
import DateFnsUtils from '@date-io/date-fns';
import fiLocale from 'date-fns/locale/fi';

import axios from 'axios';
import { IconButton } from '@material-ui/core';
import NoteAddIcon from '@material-ui/icons/NoteAdd';



function UusiTilaus (props) {

    const kirjatunut = props.kirjautunut;

    //const [tilaus, setValues] = useState({tuote: '', tilauspaiva: new Date(), toimpaiva: null, hinta: 0, tyontekija: '', asiakas: ''})

    const [tuote, setTuote] = useState('');
    const muutaTuote = (e) => {
    setTuote(e.target.value);
    };

    const [tilauspaiva, setTilauspaiva] = useState(new Date());
    const muutaPaiva = (date) =>{
        const tilpaiva = date.getFullYear()+ '-' + (date.getMonth()+1)+ '-' + date.getDate();
        console.log(tilpaiva);
        setTilauspaiva(tilpaiva);
    };

    const [toimpaiva, setTPaiva] = useState(null);
    const muutaTPaiva = (date) => {
        const toimpaiva = date.getFullYear()+ '-' + (date.getMonth()+1)+ '-' + date.getDate()
        setTPaiva(toimpaiva);
    };

    const [hinta, setHinta] = useState();
    const muutaHinta = (e) => {
    setHinta(e.target.value);
    };    

    const [tyontekija, setTyontekija] = useState('');
    const muutaTyontekija = (e) => {
    setTyontekija(e.target.value);
    };   

    const [asiakas, setAsiakas] = useState('');
    const muutaAsiakas = (e) =>{
        setAsiakas(e.target.value);
    };

    const [viesti, setViesti] = useState('');

    const [tyontekijat, setTyontekijat] = useState([]);

    const url= 'http://localhost:8080'

    const lisaaTilaus = (e) => {
        e.preventDefault();
        const formData = {
            'tuote' : tuote,
            'tilauspvm': tilauspaiva,
            'toimpvm': toimpaiva,
            'hinta': hinta,
            'tyontekija': tyontekija,
            'asiakas': asiakas,
        }
        axios.post( url + '/kanta/tilaukset/add', formData)
        .then(response => {
            if (response.status === 200){
                setTuote('')
                setTilauspaiva(new Date())
                setTPaiva(null)
                setHinta(0)
                setTyontekija('')
                setAsiakas('')
                setViesti('Lisäys onnistui')
            }else{
                setViesti('Lisäys ei onnistunut')
            }
        })
    }

    const haeTyontekijat = async() => {
        try {
            const response = await fetch (url + '/kanta/tyontekijat/all');
            const json = await response.json();
            setTyontekijat(json);
        }catch (error) {
            setTyontekijat([]);
            console.log('Haku ei onnistunut');
        }
    };

    useEffect(() =>{
        haeTyontekijat();
    }, []);
    

    if (kirjatunut){
    return (
        <div>
            <Paper>
                <form style={{padding:20}}>

                <TextField 
                required
                fullWidth 
                label='Tuote' 
                value={tuote}
                onChange = {(e) => muutaTuote(e)}/>

                <MuiPickersUtilsProvider utils={DateFnsUtils} locale={fiLocale} >
                    <KeyboardDatePicker label='Tilauspäivä' name='tilauspaiva'
                    fullWidth={true} required
                    value={tilauspaiva}
                     onChange={muutaPaiva}
                    format='dd.MM.yyyy' />
                </MuiPickersUtilsProvider>

                
                <InputLabel id="tyont">Työntekijä</InputLabel>
                <Select
                label='Työntekijä'
                required
                fullWidth
                value={tyontekija}
                onChange= {(e) => muutaTyontekija(e)}
                labelId='tyont'
                >
                    {tyontekijat.map(tyontekija => {
                        return(
                            <MenuItem value={tyontekija.etunimi + ' ' + tyontekija.sukunimi }>{tyontekija.etunimi} {tyontekija.sukunimi}</MenuItem>
                        )
                    })}
                    
                </Select>
                

                <MuiPickersUtilsProvider utils={DateFnsUtils} locale={fiLocale}>
                    <KeyboardDatePicker label='Toimituspäivä' name='toimpaiva'
                    fullWidth={true}                    
                    value={toimpaiva}
                    onChange={ muutaTPaiva}
                    format='dd.MM.yyyy' />
                </MuiPickersUtilsProvider>

                <TextField 
                required
                fullWidth 
                label='Hinta' 
                value={hinta}
                onChange = {(e) => muutaHinta(e)}/>

                <TextField 
                required
                fullWidth 
                label='Asiakas' 
                value={asiakas}
                onChange = {(e) => muutaAsiakas(e)}/>

                </form>
                <IconButton
                onClick={lisaaTilaus}>
                    <NoteAddIcon/>
                    Lisää tilaus
                </IconButton>

                <Typography>{viesti}</Typography>
            </Paper>
        </div>
    )}
    else {
        return(
            <Typography>
                Ole hyvä ja kirjaudu sisään ensin!
            </Typography>
        )
    }
};
export default UusiTilaus;
